package week3.day1;

public class ToGetCharacterIndexOfGivenString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str = "Java Exercise";
		int index1 = str.charAt(5);
		int index2 = str.charAt(11);
		System.out.println("The chararcter at position 5 is" + (char) index1);
		System.out.println("The chararcter at position 11 is" + (char) index2);
	}

}
